import React from "react";
import "./TextArea.css"

export default function TextArea() {

    function resizeInput() {
        const input = document.getElementById('expandingInput');
        input.style.height = input.value.length + 'ch';
      }

    return(
    <div className="textarea">
        <input type="text" placeholder="type your text here" className="input" id='expandingInput' onInput={resizeInput}/>
    </div>

    )
}
