import React from "react";
import "./TextArea.css"
import { useState } from "react";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faPaperPlane } from '@fortawesome/free-solid-svg-icons';
import Message from "./Message";
import axios from "axios";
import { useEffect } from "react";

export default function TextArea() {

const [details, setDetails] = useState({
    search_data:'',
});
const [text, setText] = useState({
    search_data:'',
});

const handleTextareaChange = (e) => {
    const { id, value } = e.target;
    // console.log(value);
    setText((prevText) => (
        // value
        {
      ...prevText,
      [id]: value,
    }
    ));
  };

const handleSubmit = (e) => {
    e.preventDefault();
    console.log(text);
    axios.post("api/search/",text)
    .then(res =>
        {window.location.href='/'}
    )
    .catch(err => console.log(err));
}

useEffect(() => {
    // Make an HTTP request to fetch the card image from the backend
    axios.get('/api/search/')
      .then((response) => {
        // Assuming the response contains the image URL
        console.log(response.data);
        setDetails(response.data[0]);
      })
      .catch((error) => {
        console.error('', error);
      });
  }, []);

    return(
    <div className="textarea">
        <div className="messagearea">
        {details.map((details,index) =>{
            <div className="tujamazaa" key ={index} >
                <Message text={details.search_data}/>
            </div>
        })}
        </div>
        <div className="inputbutton">
            <input type="text" id='search_data' placeholder="type your text here" className="input" onChange={handleTextareaChange}
            value={text.search_data} />
            <button onClick={handleSubmit} className="ad">
                <FontAwesomeIcon icon={faPaperPlane} />
            </button>
        </div>
    </div>

    )
}
